INSERT INTO PROBLEM(id, subject_id, subject_category_id, question, answer, keyword, registered, status, created_at, updated_at) VALUES(1, 1, 2, '문맥 전환이 무엇인가?','CPU가 이전 상태의 프로세스를 PCB에 보관하고, 또 다른 프로세스를 PCB에서 읽어 레지스터에 적재하는 과정', 'PCB', true, 'ACTIVE', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO PROBLEM(id, subject_id, subject_category_id, question, answer, keyword, registered, status, created_at, updated_at) VALUES(2, 1, 2, '프로세스란 무엇인가?','프로세스는 실행 중인 프로그램으로 디스크로부터 메모리에 적재되어 CPU의 할당을 받을 수 있는 것을 말한다', 'CPU', true, 'ACTIVE', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO PROBLEM(id, subject_id, subject_category_id, question, answer, keyword, registered, status, created_at, updated_at) VALUES(3, 1, 2, '프로세스가 CPU 를 할당받아 작업을 처리하다가 프로세스 전환이 발생한다면? ','진행하던 작업을 저장하고 CPU 를 반환해야 하는데, 이때 작업의 진행 상황을 모두 PCB 에 저장하게 된다', 'PCB', true, 'ACTIVE', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO FAQ(id, problem_id, question, answer, status, created_at, updated_at) VALUES(1, 1, 'PCB란?', 'PCB 는 특정프로세스에 대한 중요한 정보를 저장하고 있는 운영체제의 자료구조이다', 'ACTIVE', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO FAQ(id, problem_id, question, answer, status, created_at, updated_at) VALUES(2, 2, '프로세스가 포함하는 것은?', '프로세스는 함수의 매개변수, 복귀 주소와 로컬 변수와 같은 임시 자료를 갖는 프로세스 스택과 전역 변수들을 수록하는 데이터 섹션을 포함한다', 'ACTIVE', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO FAQ(id, problem_id, question, answer, status, created_at, updated_at) VALUES(3, 3, 'PCB에 저장되는 정보는?', '프로세스 식별자(Process ID, PID), 프로세스 상태, 프로그램 카운터, CPU 레지스터, CPU 스케쥴링 정보, 메모리 관리 정보, 입출력 상태 정보, 어카운팅 정보', 'ACTIVE', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
